import React, { useEffect } from "react";
import { Link, Outlet } from "react-router-dom";

// CSS
import "./style.scss"; // Importez le CSS de cette manière

function Template() {
  useEffect(() => {
    // Utilisez useEffect pour manipuler le DOM après le rendu du composant
    const menu = document.querySelector(".menu");
    const navbar = document.querySelector(".nav-links");

    menu.addEventListener('click', () => {
      navbar.classList.toggle('mobile-menu');
    });

    // Nettoyez l'écouteur d'événement lors du démontage du composant
    return () => {
      menu.removeEventListener('click', () => {
        navbar.classList.toggle('mobile-menu');
      });
    };
  }, []); // Le tableau vide en tant que deuxième argument signifie que useEffect ne s'exécute qu'une seule fois après le montage initial du composant.

  return (
    <>
      <header>
        <img src="/images/logo.png" alt="logo" />
        <nav className="navbar">
          <ul>
            <li><Link to="/" className="accueil"> Accueil</Link></li>
            <li><Link to="/Concept" className="Concept"> Concept</Link></li>
            <li><Link to="/Tarifs" className="tarifs"> Tarifs</Link></li>
            <li><Link to="/Contact" className="contact"> Nous contacter</Link></li>
            <li><Link to="/About" className="about"> À propos</Link></li>
          </ul>
          <img src="/images/Menu.png" alt="menu" className="menu" />
        </nav>
      </header>
      <section>
        <Outlet />
      </section>
      <footer>
        {/* Footer */}
        {/* ... Votre contenu de pied de page ici */}
      </footer>
    </>
  );
}

export default Template;
